mapper.select:
SELECT u.id userId, u.name username, q.ID questionId, q.TITLE title, vh.gmt_Modified gmtModified
FROM VIEW_HISTORY vh
JOIN QUESTION q ON vh.QUESTION_ID = q.ID
JOIN USER u ON q.CREATOR = u.ID
where vh.viewer = #{userId}
limit #{offset},#{size};

mapper.insert:
insert into QUESTION (title,description,creator,gmt_create,gmt_modified)
values (#{title},#{description},#{creator},#{gmtCreate},#{gmtModified});

    select last_insert_id();

insert into USER (`name` ,`salt`,`password`,`token`,`avatar_url`,`phone_number`,``gmt_create`,`gmt_modified`)
values (#{name},#{salt},#{password},#{token}, #{avatar_url}, #{phone_number}, #{gmtCreate},#{gmtModified});

    select last_insert_id();

mapper.select:
select count(*)
from QUESTION


        and title regexp #{search}

select *
from QUESTION


        and title regexp #{search}


order by gmt_create desc limit #{offset},#{size}
